mapper.update:
UPDATE inforerrorboard
SET views=#{views}+1
WHERE writeno=#{writeno}

mapper.select:
SELECT COUNT(*) AS cnt
FROM reply
WHERE isvisible=1 AND boardname="inforerrorboard" AND wno=#{writeno}

mapper.update:
UPDATE inforerrorboard
SET title=#{title}, content=#{content}
WHERE writeno=#{writeno}
