mapper.select:
SELECT * FROM 
	(
		SELECT *
		FROM teamRecord 
		WHERE DATEDIFF(gdate, date(NOW()))> 0 
			AND  ateamname != '경기없음'
		and month(gdate)=#{month}
		ORDER BY gdate LIMIT #{start}, 10
	) AS s, 
	(
		SELECT COUNT(*) AS totalcnt FROM teamRecord	WHERE (DATEDIFF(gdate, date(NOW()))>0 AND  ateamname != '경기없음')
		and month(gdate)=#{month} 
	) AS c

SELECT * FROM 
	(
		SELECT *
		FROM teamRecord 
		WHERE DATEDIFF(gdate, date(NOW()))< 0 
			AND  ateamname != '경기없음'
		and month(gdate)=#{month} 
		ORDER BY gdate desc LIMIT #{start}, 10
	) AS s, 
	(
		SELECT COUNT(*) AS totalcnt FROM teamRecord	WHERE DATEDIFF(gdate, date(NOW()))<0
		and month(gdate)=#{month}  
	) AS c
